import { useEffect, useState } from 'react'
import { NotePost } from './components_notesPost/NotePost'
import { noteService } from  './modules_notesPost/noteService'
import { Notification } from './components_notesPost/Notification'
import { NewNote } from './components_notesPost/NewNote'

export const FormsAxiosPostModule = () => {

    const [ notes, setNotes ] = useState([])
    const [ newNote, setNewNote ] = useState('')
    const [ showAll, setShowAll ] = useState(true)
    const [ errorMessage, setErrorMessage ] = useState('') 
    const [ booleanImportance, setBooleanImportance ] = useState(true)
    //const [id, setId] = useState("") 

    useEffect(() => {
       noteService.getAll()
       .then(initialNotes => setNotes(initialNotes))
       }, [newNote])

    const handleNoteChange = (event) => {
        setNewNote(event.target.value)
    }

    const addNote = (event) => {
        event.preventDefault()
        const noteObject = {
          content: newNote,
          date: new Date().toISOString(),
          important: Math.random() < 0.5,
        }
        var testing = noteService.create(noteObject)
        console.log("TESTING", testing) 
    }

    //const notesToShow = showAll? notes: notes.filter(note => note.important === true)

    const toggleImportanceOf = (id) => {
        const note =  notes.filter(n => n.id === id)
        console.log("note", note)
        notes.forEach(e=> e.importance)
        const exampleNotesId = notes.map(note=> note.id !== id? {...note} : {...note, important:booleanImportance})
        console.log("EXAMP N ID", exampleNotesId)       
        noteService.update(id, exampleNotesId)
    }
        //     .catch(error => {    
        //       console.log("CATCHING ERRORS")           
        //       setErrorMessage(`the note '${note.content}' was already deleted from server`)  
        //       console.log("ERROR MESSAGE", errorMessage)           
        //       setTimeout(()=>setErrorMessage(null),5000)
        //       setNotes(notes.filter(n => n.id !== id))
        //     })
    //}

    const changeImportance = () => {
      return setShowAll(!showAll)
    }

  return (
    <div>
        <h1>Blog Notes</h1>
        <Notification message={errorMessage}></Notification>
        <NewNote
          addNote={addNote}
          handleNoteChange={handleNoteChange}
          showAll={showAll}
          changeImportance={changeImportance}>
        </NewNote>    

        <NotePost
          notes={notes} 
          toggleImportanceOf={toggleImportanceOf}>
        </NotePost>

    </div>
  )
}
