export const NotePost = (props) => {
    const label = props.notes[0].important? 'make not important' : 'make important'

    return <>
    {props.notes.map(e=>{
        <>
            <li>
            {e.content}
            </li>
            <br></br>
            <button onClick={toggleImportance(e.id)}>{label}</button>
            <br></br>
            <br></br>
        </>
    })}

    </>

  }
